import React, { useEffect, useState } from 'react';
import axios from 'axios';
import styled from 'styled-components';
import { Line } from 'react-chartjs-2';
import 'chart.js/auto';

const Container = styled.div`
  display: flex;
  height: 100vh;
`;

const Sidebar = styled.div`
  width: 400px; /* Adjusted width */
  background: #f0f0f0;
  padding: 20px;
  border-right: 1px solid #ccc;
`;

const ProductList = styled.ul`
  list-style-type: none;
  padding: 0;
  margin: 0;
`;

const ProductItem = styled.li`
  background: ${props => (props.selected ? '#d0d0d0' : '#fff')};
  margin: 10px 0;
  padding: 10px;
  border-radius: 5px;
  box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
  cursor: pointer;
  transition: background 0.3s;
  display: flex;
  justify-content: space-between;
  align-items: center;
  &:hover {
    background: #d0d0d0;
  }
`;

const DetailsPanel = styled.div`
  flex-grow: 1;
  padding: 20px;
  background: #fff;
  margin-left: 20px;
  border-radius: 5px;
  box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
  display: flex;
  flex-direction: column;
  align-items: flex-start; /* Align items to the start */
  justify-content: flex-start; /* Justify content to the start */
`;

const PriceBox = styled.div`
  background: #011E3E;
  color: #fff;
  padding: 20px;
  border-radius: 5px;
  font-size: 1.5rem;
  margin-bottom: 20px;
  text-align: center;
  width: 100%; /* Full width */
  margin-top: 10px; /* Top margin */
`;

const ProductLink = styled.a`
  color: #007bff;
  text-decoration: none;
  font-size: 1.2rem;
  &:hover {
    text-decoration: underline;
  }
`;

const DeleteButton = styled.button`
  background-color: red;
  color: white;
  border: none;
  border-radius: 4px;
  padding: 5px 10px;
  cursor: pointer;
  &:hover {
    background-color: darkred;
  }
`;

const Dashboard = ({ userId }) => {
  const [products, setProducts] = useState([]);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [currentPrice, setCurrentPrice] = useState(null);
  const [priceHistory, setPriceHistory] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    if (userId) {
      const fetchProducts = async () => {
        try {
          const response = await axios.get(`http://ec2-34-229-153-14.compute-1.amazonaws.com:8000/products/${userId}`);
          setProducts(response.data);
        } catch (error) {
          setError('Eroare la preluarea produselor');
        }
      };

      fetchProducts();
    }
  }, [userId]);

  useEffect(() => {
    if (selectedProduct) {
      const fetchCurrentPrice = async () => {
        try {
          const response = await axios.get(`http://ec2-34-229-153-14.compute-1.amazonaws.com:8000/getpricesforproduct/${selectedProduct.id}`);
          if (response.data.length > 0) {
            setCurrentPrice(response.data[0].price);
            setPriceHistory(response.data);
          } else {
            setCurrentPrice('N/A'); // Handle case where no price is available
            setPriceHistory([]);
          }
        } catch (error) {
          console.error('Eroare la preluarea prețurilor:', error);
        }
      };

      fetchCurrentPrice();
    }
  }, [selectedProduct]);

  const handleDelete = async (productId) => {
    try {
      await axios.delete(`http://ec2-34-229-153-14.compute-1.amazonaws.com:8000/delete-product/${productId}`);
      setProducts(products.filter(product => product.id !== productId));
      setSelectedProduct(null);
    } catch (error) {
      console.error('Eroare la ștergerea produsului:', error);
    }
  };

  const chartData = {
    labels: priceHistory.map(price => new Date(price.date_extracted).toLocaleDateString()),
    datasets: [
      {
        label: 'Preț istoric',
        data: priceHistory.map(price => parseFloat(price.price.replace(',', '.').replace(' Lei', ''))),
        fill: false,
        backgroundColor: '#007bff',
        borderColor: '#007bff',
      },
    ],
  };

  const chartOptions = {
    scales: {
      x: {
        type: 'time',
        time: {
          unit: 'day',
        },
      },
      y: {
        beginAtZero: true,
      },
    },
  };

  if (error) {
    return <p>{error}</p>;
  }

  return (
    <Container>
      <Sidebar>
        <h2>Produsele tale</h2>
        <ProductList>
          {products.map(product => (
            <ProductItem
              key={product.id}
              selected={selectedProduct && selectedProduct.id === product.id}
              onClick={() => setSelectedProduct(product)}
            >
              <p><strong>{product.product_name}</strong></p>
              <DeleteButton onClick={(e) => {
                e.stopPropagation();
                handleDelete(product.id);
              }}>Șterge</DeleteButton>
            </ProductItem>
          ))}
        </ProductList>
      </Sidebar>
      {selectedProduct && (
        <DetailsPanel>
          {currentPrice && (
            <PriceBox>
              Preț curent: {currentPrice}
            </PriceBox>
          )}
          <ProductLink href={selectedProduct.product_url} target="_blank" rel="noopener noreferrer">
            Vizitează produsul
          </ProductLink>
          <div style={{ width: '100%', marginTop: '20px' }}>
            <Line data={chartData} options={chartOptions} />
          </div>
        </DetailsPanel>
      )}
    </Container>
  );
};

export default Dashboard;
