import React, { useEffect, useState } from 'react';
import axios from 'axios';
import styled from 'styled-components';

const Container = styled.div`
  display: flex;
`;

const Sidebar = styled.div`
  width: 600px; 
  background: #f0f0f0;
  padding: 20px;
`;

const ProductList = styled.ul`
  list-style-type: none;
  padding: 0;
  margin: 0;
`;

const ProductItem = styled.li`
  background: ${props => (props.selected ? '#d0d0d0' : '#fff')};
  margin: 10px 0;
  padding: 10px;
  border-radius: 5px;
  box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
  cursor: pointer;
  &:hover {
    background: #d0d0d0;
  }
`;

const DetailsPanel = styled.div`
  flex-grow: 1;
  padding: 20px;
  background: #fff;
  margin-left: 20px;
  border-radius: 5px;
  box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
`;

const Products = ({ userId }) => {
  const [products, setProducts] = useState([]);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [prices, setPrices] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    if (userId) {
      const fetchProducts = async () => {
        try {
          const response = await axios.get(`http://ec2-34-229-153-14.compute-1.amazonaws.com:8000/products/${userId}`);
          setProducts(response.data);
        } catch (error) {
          setError('Eroare la preluarea produselor');
        }
      };

      fetchProducts();
    }
  }, [userId]);

  useEffect(() => {
    if (selectedProduct) {
      const fetchPrices = async () => {
        try {
          const response = await axios.get(`http://ec2-34-229-153-14.compute-1.amazonaws.com:8000/getpricesforproduct/${selectedProduct.id}`);
          setPrices(response.data);
        } catch (error) {
          console.error('Eroare la preluarea prețurilor:', error);
        }
      };

      fetchPrices();
    }
  }, [selectedProduct]);

  if (error) {
    return <p>{error}</p>;
  }

  return (
    <Container>
      <Sidebar>
        <h2>Produsele tale</h2>
        <ProductList>
          {products.map(product => (
            <ProductItem
              key={product.id}
              selected={selectedProduct && selectedProduct.id === product.id}
              onClick={() => setSelectedProduct(product)}
            >
              <p><strong>{product.product_name}</strong></p>
              {prices.length > 0 && selectedProduct?.id === product.id && (
                <p>Preț curent: {prices[0].price}</p>
              )}
            </ProductItem>
          ))}
        </ProductList>
      </Sidebar>
      {selectedProduct && (
        <DetailsPanel>
          <h2>Detalii produs</h2>
          <p><strong>Link:</strong> <a href={selectedProduct.product_url} target="_blank" rel="noopener noreferrer">{selectedProduct.product_url}</a></p>
          {prices.length > 0 && (
            <>
              <h3>Prețuri</h3>
              <ul>
                {prices.map((price, index) => (
                  <li key={index}>
                    {price.price} - {new Date(price.date_extracted).toLocaleString()}
                  </li>
                ))}
              </ul>
            </>
          )}
        </DetailsPanel>
      )}
    </Container>
  );
};

export default Products;
