import React, { useState } from 'react';
import { Routes, Route, Link } from 'react-router-dom';
import styled from 'styled-components';
import Register from './components/Register';
import Login from './components/Login';
import Dashboard from './components/Dashboard';
import AddProduct from './components/AddProduct';
import Header from './components/Header';

const Container = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 90vh;
  background-color: #f0f0f0;
`;

const Card = styled.div`
  background: white;
  border-radius: 10px;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.2);
  width: 400px;
  max-width: 100%;
  padding: 20px;
  text-align: center;
`;

const ToggleLink = styled(Link)`
  margin-top: 20px;
  display: block;
  color: #007bff;
  cursor: pointer;
  text-decoration: none;
  &:hover {
    text-decoration: underline;
  }
`;

const App = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [userId, setUserId] = useState(null);

  const handleLoginSuccess = (id) => {
    setIsAuthenticated(true);
    setUserId(id);
  };

  const handleLogout = () => {
    setIsAuthenticated(false);
    setUserId(null);
  };

  return (
    <>
      <Header isAuthenticated={isAuthenticated} onLogout={handleLogout} />
      <Routes>
        <Route
          path="/"
          element={
            <Container>
              <Card>
                <Login onLoginSuccess={handleLoginSuccess} />
                <ToggleLink to="/register">Nu aveți un cont? Înregistrați-vă</ToggleLink>
              </Card>
            </Container>
          }
        />
        <Route
          path="/register"
          element={
            <Container>
              <Card>
                <Register />
                <ToggleLink to="/">Aveți deja un cont? Autentificați-vă</ToggleLink>
              </Card>
            </Container>
          }
        />
        <Route path="/dashboard" element={<Dashboard />} />
        <Route path="/adauga-produs" element={<Container><Card><AddProduct userId={userId} /></Card></Container>} />
      </Routes>
    </>
  );
};

export default App;
