import React, { useState } from 'react';
import axios from 'axios';
import styled from 'styled-components';

const Form = styled.form`
  display: flex;
  flex-direction: column;
`;

const Input = styled.input`
  padding: 10px;
  margin: 10px 0;
  border: 1px solid #ccc;
  border-radius: 4px;
`;

const Button = styled.button`
  padding: 10px;
  background-color: #011E3E; /* Update button background color */
  color: white;
  border: none;
  border-radius: 4px;
  cursor: pointer;
  &:hover {
    background-color: #003366; /* Darken on hover */
  }
`;

const Message = styled.p`
  color: ${props => (props.error ? 'red' : 'green')};
`;

const Login = ({ onLoginSuccess }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://ec2-44-222-228-87.compute-1.amazonaws.com:8000/login', {
        username,
        password,
      });
      setMessage(response.data);
      if (response.data === 'Login successful') {
        onLoginSuccess();
      }
    } catch (error) {
      if (error.response) {
        setMessage(error.response.data);
      } else if (error.request) {
        setMessage('Nu am primit răspuns de la server.');
      } else {
        setMessage('Eroare: ' + error.message);
      }
    }
  };

  return (
    <div>
      <h2>Autentificare</h2>
      <Form onSubmit={handleSubmit}>
        <Input type="text" placeholder="Nume de utilizator" value={username} onChange={(e) => setUsername(e.target.value)} required />
        <Input type="password" placeholder="Parolă" value={password} onChange={(e) => setPassword(e.target.value)} required />
        <Button type="submit">Autentificare</Button>
      </Form>
      {message && <Message error>{message}</Message>}
    </div>
  );
};

export default Login;
