import React, { useState, useEffect } from 'react';
import axios from 'axios';
import styled from 'styled-components';

const Container = styled.div`
  display: flex;
`;

const ProductList = styled.div`
  width: 300px;
  padding: 20px;
  border-right: 1px solid #ccc;
`;

const ProductItem = styled.div`
  padding: 10px;
  cursor: pointer;
  &:hover {
    background-color: #f0f0f0;
  }
`;

const ProductDetails = styled.div`
  flex: 1;
  padding: 20px;
`;

const Products = () => {
  const [products, setProducts] = useState([]);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [prices, setPrices] = useState([]);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await axios.get('http://ec2-34-229-153-14.compute-1.amazonaws.com:8000/getproducts', {
          headers: { Authorization: localStorage.getItem('token') },
        });
        setProducts(response.data);
      } catch (error) {
        console.error('Eroare la preluarea produselor:', error);
      }
    };

    fetchProducts();
  }, []);

  useEffect(() => {
    if (selectedProduct) {
      const fetchPrices = async () => {
        try {
          const response = await axios.get(`http://ec2-34-229-153-14.compute-1.amazonaws.com:8000/getpricesforproduct/${selectedProduct.id}`, {
            headers: { Authorization: localStorage.getItem('token') },
          });
          setPrices(response.data);
        } catch (error) {
          console.error('Eroare la preluarea prețurilor:', error);
        }
      };

      fetchPrices();
    }
  }, [selectedProduct]);

  return (
    <Container>
      <ProductList>
        {products.map((product) => (
          <ProductItem key={product.id} onClick={() => setSelectedProduct(product)}>
            {product.product_name}
          </ProductItem>
        ))}
      </ProductList>
      <ProductDetails>
        {selectedProduct && (
          <div>
            <h2>{selectedProduct.product_name}</h2>
            <p>Link: <a href={selectedProduct.product_url} target="_blank" rel="noopener noreferrer">{selectedProduct.product_url}</a></p>
            {prices.length > 0 && (
              <div>
                <h3>Preț curent: {prices[0].price}</h3>
                <p>Data: {new Date(prices[0].date_extracted).toLocaleString()}</p>
              </div>
            )}
          </div>
        )}
      </ProductDetails>
    </Container>
  );
};

export default Products;
