import React, { useState, useEffect } from 'react';
import axios from 'axios';
import styled from 'styled-components';

const Form = styled.form`
  display: flex;
  flex-direction: column;
`;

const Input = styled.input`
  padding: 10px;
  margin: 10px 0;
  border: 1px solid #ccc;
  border-radius: 4px;
`;

const Button = styled.button`
  padding: 10px;
  background-color: #007bff;
  color: white;
  border: none;
  border-radius: 4px;
  cursor: pointer;
  &:hover {
    background-color: #0056b3;
  }
`;

const Message = styled.p`
  color: ${props => (props.error ? 'red' : 'green')};
`;

const AddProduct = ({ userId }) => {
  const [productName, setProductName] = useState('');
  const [productUrl, setProductUrl] = useState('');
  const [message, setMessage] = useState('');

  useEffect(() => {
    if (!userId) {
      setMessage('Utilizator neautentificat');
    }
  }, [userId]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!userId) {
      setMessage('Utilizator neautentificat');
      return;
    }
    try {
      const response = await axios.post('http://ec2-34-229-153-14.compute-1.amazonaws.com:8000/add-product', {
        userId,
        productName,
        productUrl,
      });
      setMessage(response.data);
    } catch (error) {
      if (error.response) {
        setMessage(error.response.data);
      } else if (error.request) {
        setMessage('Nu am primit răspuns de la server.');
      } else {
        setMessage('Eroare: ' + error.message);
      }
    }
  };

  return (
    <div>
      <h2>Adaugă Produs</h2>
      <Form onSubmit={handleSubmit}>
        <Input type="text" placeholder="Nume produs" value={productName} onChange={(e) => setProductName(e.target.value)} required />
        <Input type="text" placeholder="URL produs" value={productUrl} onChange={(e) => setProductUrl(e.target.value)} required />
        <Button type="submit">Adaugă</Button>
      </Form>
      {message && <Message error={message !== 'Produs adăugat cu succes'}>{message}</Message>}
    </div>
  );
};

export default AddProduct;
